export const addIframeClass = (className: string) => `.${className} {
  display: none;
  position: fixed;
  bottom: 20px;
  right: 20px;
  max-width: 440px;
  height: 85vh;
  min-height: 300px;
  max-height: 600px;
  width: 100%;
  margin: auto;
  box-sizing: border-box;
  border-radius: 16px;
  border: 0;
  box-shadow: rgba(17, 17, 26, 0.1) 0 4px 16px, rgba(17, 17, 26, 0.1) 0 8px 32px;
  z-index: 50;
}`;

export const addTriggerClass = (className: string) => `
@keyframes trigger {
  0% {
    transform: scale(1);
  }
  20% {
    transform: scale(1.05);
  }
  100% {
    transform: scale(1);
  }
}

.${className} {
  display: block;
  position: fixed;
  bottom: 0;
  right: 0;
  margin: 24px;
  width: 60px;
  height: 60px;
  border: 6px solid #ffffff;
  border-radius: 50%;
  box-shadow: rgba(17, 17, 26, 0.1) 0 4px 16px, rgba(17, 17, 26, 0.1) 0 8px 32px;
  background-color: #2a2a2a;
  background-image: url('data:image/jpeg;base64,');
  background-size: cover;
  background-position: center;
  transition: 0.2s all ease-in-out;
  cursor: pointer;
}

.${className}:hover {
  box-shadow: rgb(17, 17, 26, .3) 0 4px 26px, rgb(17, 17, 26, 0.1) 0 8px 32px;
  transform: scale(1.05);
}

.${className}.is-animated {
  animation: trigger 1.5s infinite;
  animation-fill-mode: both;
}`;
